<?php/*** Template Name: Home Page*/?><?php get_header(); ?>                       <section class="main">                	                	<!-- Feature Image Field Group -->                	<header>                		<!-- Feature Image -->                        <figure><img src="<?php the_field('feature_image') ?>" >		                       		<figcaption>	                       		 	<?php the_field('feature_image_text') ?>                        	</figcaption>                         </figure>                    </header>                                                          <!-- Home Main Field Group -->                    <article id="intro">                    	 	<!-- Home Main About -->                       	 	<?php the_field('home_about_me') ?>                   	</article>                    	 										<section id="blog">	                     	                     	<h4>Blog Updates</h4>	            					<?php					$args = array( 						'post_type' => 'post', 						'posts_per_page' => 3,						'orderby' => 'post_date' );										$the_query = new WP_Query( $args );					// The Loop					if ( $the_query->have_posts() ) {					echo '<ul>';						while ( $the_query->have_posts() ) {						$the_query->the_post();						echo '<li><h5><a href="' . get_permalink($post->ID) . '">' . get_the_title($post->ID) . '</a></h5><div class="content">' . get_the_post_thumbnail($post->ID, 'thumbnail') . '<p>' . get_the_excerpt($post->ID) . '</p></div></li>';						}					echo '</ul>';				} else {					// no posts found				}					/* Restore original Post Data */				wp_reset_postdata();    				    				?>    		    				</section>    				    				    				    				<section id="performancehome">                    	<h4>Upcoming Performances</h4>                     											<?php 							// args							$args = array(								'numberposts'	=> 3,								'post_type'		=> 'performance-listing',								'orderby'		=> 'post_date',								);							// query							$the_query = new WP_Query( $args );							?>								<?php if( $the_query->have_posts() ): ?>								<ul>								<?php while( $the_query->have_posts() ) : $the_query->the_post(); ?>								<!-- Repeater loop 1 -->																<?php									// check if the repeater field has rows of data									if( have_rows('performance_listing') ):						 									// loop through the rows of data   							 		while ( have_rows('performance_listing') ) : the_row();		    								?>   									   									   									<li>										<a href="<?php the_permalink(); ?>">										<?php the_title(); ?>										</a>   									<!-- Repeater loop 2 -->   									<?php										// check if the repeater field has rows of data										if( have_rows('event_details') ):						 										// loop through the rows of data   						 				while ( have_rows('event_details') ) : the_row();   									?>    																	<p><?php the_sub_field(detail_line_1); ?></p>									</li>																																						<?php endwhile; ?> 									<?php endif; ?> 								<?php endwhile; ?> 							<?php endif; ?>         							<?php endwhile; ?>				</ul>			<?php endif; ?>		<?php wp_reset_query();	 // Restore global post data stomped by the_post(). ?>				    				    				</section>                      	           	 </section><?php get_footer(); ?>